import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Favorite as FavoriteIcon, ChatBubbleOutline as CommentIcon } from '@mui/icons-material';
import { supabase } from '../supabaseClient';
import CommentsModal from './CommentsModal';
import '../styles/Post.css';

function Post({ username, userAvatar, image, caption, postId }) {
  const navigate = useNavigate();
  const [showComments, setShowComments] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const [likesCount, setLikesCount] = useState(0);

  useEffect(() => {
    // Fetch latest likes count from Supabase
    const fetchLikes = async () => {
      const { data, error } = await supabase
        .from('posts')
        .select('likes')
        .eq('id', postId)
        .single();

      if (error) {
        console.error('Error fetching likes:', error.message);
      } else {
        setLikesCount(data.likes);
      }
    };

    fetchLikes();
  }, [postId]);

  const handleLike = async () => {
  if (!postId) {
    console.error('No postId provided!');
    return;
  }

  const newLikes = isLiked ? likesCount - 1 : likesCount + 1;

  const { error } = await supabase
    .from('posts')
    .update({ likes: newLikes })
    .eq('id', postId);

  if (error) {
    console.error('Supabase update failed:', error.message);
    return;
  }

  console.log(`Updated likes to ${newLikes} for post ${postId}`);
  setLikesCount(newLikes);
  setIsLiked(!isLiked);
};


  const handleProfileClick = () => {
    navigate(`/profile/${username}`);
  };

  return (
    <div className="post">
      <div className="post-header">
        <img 
          className="post-avatar"
          src={userAvatar}
          alt={username}
          onClick={handleProfileClick}
          style={{ cursor: 'pointer' }}
        />
        <span 
          className="post-username"
          onClick={handleProfileClick}
          style={{ cursor: 'pointer' }}
        >
          {username}
        </span>
      </div>

      <img
        className="post-image"
        src={image}
        alt="Post content"
      />

      <div className="post-actions">
        <button 
          className={`action-button ${isLiked ? 'liked' : ''}`}
          onClick={handleLike}
        >
          <FavoriteIcon />
        </button>
        <button 
          className="action-button"
          onClick={() => setShowComments(true)}
        >
          <CommentIcon />
        </button>
      </div>

      <div className="post-likes">
        {likesCount} likes
      </div>

      <div className="post-caption">
        <span 
          className="post-username-caption"
          onClick={handleProfileClick}
          style={{ cursor: 'pointer' }}
        >
          {username}
        </span>
        {caption}
      </div>

      {showComments && (
        <CommentsModal
          postId={postId}
          onClose={() => setShowComments(false)}
        />
      )}
    </div>
  );
}

export default Post;
