import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { supabase } from '../supabaseClient'; 
import {
  Favorite as FavoriteIcon,
  ChatBubbleOutline as CommentIcon,
  Share as ShareIcon,
} from '@mui/icons-material';
import CommentsModal from './CommentsModal';
import '../styles/Post.css';

function Post({ username, userAvatar, image, caption, likes: initialLikes, postId }) {
  const [showComments, setShowComments] = useState(false);
  const [isLiked, setIsLiked] = useState(false);
  const [likesCount, setLikesCount] = useState(initialLikes);
  const [userId, setUserId] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user }, error } = await supabase.auth.getUser();
      if (error) {
        console.error('Error fetching user:', error.message);
      } else if (user) {
        setUserId(user.id);
        console.log('Fetched userId:', user.id);
      }
    };
    fetchUser();
  }, []);

  const handleLike = async () => {
    if (!userId) {
      console.error('User not logged in or userId not loaded yet');
      return;
    }

    console.log('handleLike clicked, isLiked:', isLiked);

    if (isLiked) {
      const { error } = await supabase
        .from('likes')
        .delete()
        .eq('post_id', postId)
        .eq('user_id', userId);

      if (error) {
        console.error('Error unliking post:', error.message);
      } else {
        console.log('Successfully unliked');
        setLikesCount((prev) => prev - 1);
        setIsLiked(false);
      }
    } else {
      const { error } = await supabase
        .from('likes')
        .insert([{ post_id: postId, user_id: userId }]);

      if (error) {
        console.error('Error liking post:', error.message);
      } else {
        console.log('Successfully liked');
        setLikesCount((prev) => prev + 1);
        setIsLiked(true);
      }
    }
  };

  const handleProfileClick = () => {
    navigate(`/profile/${username}`);
  };

  return (
    <div className="post">
      <div className="post-header">
        <img 
          className="post-avatar"
          src={userAvatar}
          alt={username}
          onClick={handleProfileClick}
          style={{ cursor: 'pointer' }}
        />
        <span 
          className="post-username"
          onClick={handleProfileClick}
          style={{ cursor: 'pointer' }}
        >
          {username}
        </span>
      </div>

      <img
        className="post-image"
        src={image}
        alt="Post content"
      />

      {userId ? (
        <div className="post-actions">
          <button 
            className={`action-button ${isLiked ? 'liked' : ''}`}
            onClick={handleLike}
          >
            <FavoriteIcon />
          </button>
          <button 
            className="action-button"
            onClick={() => setShowComments(true)}
          >
            <CommentIcon />
          </button>
        </div>
      ) : (
        <div className="post-actions">
          <p>Loading...</p>
        </div>
      )}

      <div className="post-likes">
        {likesCount} likes
      </div>

      <div className="post-caption">
        <span 
          className="post-username-caption"
          onClick={handleProfileClick}
          style={{ cursor: 'pointer' }}
        >
          {username}
        </span>
        {caption}
      </div>

      {showComments && (
        <CommentsModal
          postId={postId}
          onClose={() => setShowComments(false)}
        />
      )}
    </div>
  );
}

export default Post;
