import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import { usePosts } from '../contexts/PostsContext';
import { formatDistanceToNow } from 'date-fns';
import { Heart, MessageCircle, Share2, MoreHorizontal, Bookmark } from 'lucide-react';
import CommentsModal from './CommentsModal';
import '../styles/Post.css';

const Post = ({ post }) => {
  const navigate = useNavigate();
  const { currentUser } = useAuth();
  const { likePost, unlikePost, deletePost } = usePosts();
  const [showComments, setShowComments] = useState(false);
  const [showOptions, setShowOptions] = useState(false);

  const handleLike = async () => {
    if (!currentUser) {
      navigate('/login');
      return;
    }
    if (post.likes.includes(currentUser.uid)) {
      await unlikePost(post.id);
    } else {
      await likePost(post.id);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      await deletePost(post.id);
    }
  };

  const handleShare = () => {
    if (navigator.share) {
      navigator.share({
        title: 'Check out this post on InstaClone',
        text: post.caption,
        url: window.location.href,
      });
    } else {
      navigator.clipboard.writeText(window.location.href);
      alert('Link copied to clipboard!');
    }
  };

  return (
    <div className="post">
      <div className="post-header">
        <div className="post-user-info">
          <Link to={`/profile/${post.userId}`}>
            <img 
              src={post.userPhotoURL || '/default-avatar.png'} 
              alt={post.username} 
              className="post-avatar"
            />
          </Link>
          <Link
            to={`/profile/${post.userId}`}
            style={{ color: 'black', textDecoration: 'none', cursor: 'pointer' }}
          >
            <span className="post-username">{post.username}</span>
          </Link>
        </div>
        {currentUser && currentUser.uid === post.userId && (
          <div className="post-options">
            <button onClick={() => setShowOptions(!showOptions)}>
              <MoreHorizontal size={20} />
            </button>
            {showOptions && (
              <div className="options-dropdown">
                <button onClick={handleDelete}>Delete Post</button>
              </div>
            )}
          </div>
        )}
      </div>

      <div className="post-image-container">
        <img src={post.imageURL} alt={post.caption} className="post-image" />
      </div>

      <div className="post-actions">
        <div className="action-buttons">
          <button 
            onClick={handleLike}
            className={post.likes.includes(currentUser?.uid) ? 'liked' : ''}
          >
            <Heart 
              size={24} 
              fill={post.likes.includes(currentUser?.uid) ? 'currentColor' : 'none'} 
            />
          </button>
          <button 
            onClick={() => setShowComments(true)}
          >
            <MessageCircle size={24} />
          </button>
          <button onClick={handleShare}>
            <Share2 size={24} />
          </button>
        </div>
        <button className="save-button">
          <Bookmark size={24} />
        </button>
      </div>

      <div className="post-stats">
        <p>{post.likes.length} likes</p>
        <p>{post.comments.length} comments</p>
      </div>

      <div className="post-caption">
        <span className="caption-username">{post.username}</span>
        <span className="caption-text">{post.caption}</span>
      </div>

      {post.comments.length > 0 && (
        <button 
          className="view-comments"
          onClick={() => setShowComments(true)}
        >
          View all {post.comments.length} comments
        </button>
      )}

      <p className="post-timestamp">
        {formatDistanceToNow(new Date(post.timestamp), { addSuffix: true })}
      </p>

      {showComments && (
        <CommentsModal
          post={post}
          onClose={() => setShowComments(false)}
        />
      )}
    </div>
  );
};

export default Post;
