import React from 'react';
import {
  Card,
  CardHeader,
  CardMedia,
  CardContent,
  CardActions,
  Avatar,
  IconButton,
  Typography,
} from '@mui/material';
import {
  Favorite as FavoriteIcon,
  ChatBubbleOutline as CommentIcon,
  Share as ShareIcon,
} from '@mui/icons-material';

function Post({ username, userAvatar, image, caption, likes }) {
  return (
    <Card sx={{ maxWidth: 600, mx: 'auto', mb: 3, borderRadius: 3 }}>
      <CardHeader
        avatar={
          <Avatar src={userAvatar} alt={username}>
            {username[0]}
          </Avatar>
        }
        title={username}
        sx={{ '& .MuiCardHeader-title': { fontWeight: 600 } }}
      />
      <CardMedia
        component="img"
        image={image}
        alt="Post"
        sx={{ 
          width: '100%',
          height: 'auto',
          maxHeight: '600px',
          objectFit: 'cover'
        }}
      />
      <CardActions disableSpacing>
        <IconButton aria-label="like">
          <FavoriteIcon />
        </IconButton>
        <IconButton aria-label="comment">
          <CommentIcon />
        </IconButton>
      </CardActions>
      <CardContent>
        <Typography variant="subtitle2" component="span" sx={{ fontWeight: 600, mr: 1 }}>
          {likes} likes
        </Typography>
        <Typography variant="body2" color="text.primary">
          <Typography component="span" sx={{ fontWeight: 600, mr: 1 }}>
            {username}
          </Typography>
          {caption}
        </Typography>
      </CardContent>
    </Card>
  );
}

export default Post;
