import React, { useEffect, useState } from 'react';
import Post from './Post';
import { supabase } from '../supabaseClient';
import '../styles/Feed.css';

function Feed() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      setLoading(true);
      // Fetch posts and join with profiles to get username and avatar_url
      const { data, error } = await supabase
        .from('posts')
        .select('id, image_url, caption, created_at, user_id, profiles(username, avatar_url)')
        .order('created_at', { ascending: false });
      if (!error && data) {
        setPosts(data);
      } else {
        setPosts([]);
      }
      setLoading(false);
    };
    fetchPosts();
  }, []);

  return (
    <div className="feed-container">
      {loading ? (
        <p>Loading feed...</p>
      ) : posts.length === 0 ? (
        <p>No posts yet. Be the first to upload!</p>
      ) : (
        posts.map(post => (
          <Post
            key={post.id}
            username={post.profiles?.username || 'Unknown'}
            userAvatar={post.profiles?.avatar_url || 'https://i.pravatar.cc/150?img=1'}
            image={post.image_url}
            caption={post.caption}
            likes={post.likes || 0}
          />
        ))
      )}
    </div>
  );
}

export default Feed;
